import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaTimes, FaCalculator } from 'react-icons/fa';
import './OrderModal.css';

interface OrderModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const OrderModal: React.FC<OrderModalProps> = ({ isOpen, onClose }) => {
  const [step, setStep] = useState<'confirm' | 'payment'>('confirm');
  const [quantity5kg, setQuantity5kg] = useState(0);
  const [quantity10kg, setQuantity10kg] = useState(0);

  const price5kg = 59;
  const price10kg = 99;
  const totalAmount = quantity5kg * price5kg + quantity10kg * price10kg;

  const handleConfirmOrder = () => {
    setStep('payment');
  };

  const handleBackToConfirm = () => {
    setStep('confirm');
  };

  const handleClose = () => {
    setStep('confirm');
    setQuantity5kg(0);
    setQuantity10kg(0);
    onClose();
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          className="modal-overlay"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={handleClose}
        >
          <motion.div
            className="modal-content"
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            onClick={(e) => e.stopPropagation()}
          >
            <button className="modal-close" onClick={handleClose}>
              <FaTimes />
            </button>

            {step === 'confirm' && (
              <div className="confirm-step">
                <h2>üìã ËÆ¢Âçï‰ø°ÊÅØÁ°ÆËÆ§</h2>
                <p className="confirm-question">ÊÇ®ÊòØÂê¶Â∑≤ÁªèÂÆåÊàêËÆ¢Âçï‰ø°ÊÅØÂ°´ÂÜôÔºü</p>
                <div className="form-reminder">
                  <p>ËØ∑Á°Æ‰øùÊÇ®Â∑≤Âú®Ë°®Âçï‰∏≠Â°´ÂÜôÔºö</p>
                  <ul>
                    <li>‚úì Êî∂Ë¥ß‰∫∫ÂßìÂêç</li>
                    <li>‚úì ËÅîÁ≥ªÁîµËØù</li>
                    <li>‚úì Êî∂Ë¥ßÂú∞ÂùÄ</li>
                    <li>‚úì ÂïÜÂìÅËßÑÊ†ºÈÄâÊã©</li>
                  </ul>
                </div>
                <div className="confirm-buttons">
                  <button className="btn-secondary" onClick={handleClose}>
                    ËøîÂõûÂ°´ÂÜô
                  </button>
                  <button className="btn-primary" onClick={handleConfirmOrder}>
                    ‚úÖ Â∑≤ÂÆåÊàêÔºåÂéª‰ªòÊ¨æ
                  </button>
                </div>
              </div>
            )}

            {step === 'payment' && (
              <div className="payment-step">
                <h2>üí∞ ‰ªòÊ¨æ‰ø°ÊÅØ</h2>
                
                <div className="calculator-section">
                  <h3><FaCalculator /> ËÆ¢ÂçïËÆ°ÁÆóÂô®</h3>
                  <div className="calculator">
                    <div className="calc-item">
                      <span className="product-name">Â∞ùÈ≤úË£Ö (5Êñ§)</span>
                      <span className="product-price">¬•{price5kg}/‰ªΩ</span>
                      <div className="quantity-control">
                        <button 
                          onClick={() => setQuantity5kg(Math.max(0, quantity5kg - 1))}
                          disabled={quantity5kg <= 0}
                        >
                          -
                        </button>
                        <span className="quantity">{quantity5kg}</span>
                        <button onClick={() => setQuantity5kg(quantity5kg + 1)}>
                          +
                        </button>
                      </div>
                      <span className="subtotal">¬•{quantity5kg * price5kg}</span>
                    </div>

                    <div className="calc-item">
                      <span className="product-name">ÂÆ∂Â∫≠Ë£Ö (10Êñ§)</span>
                      <span className="product-price">¬•{price10kg}/‰ªΩ</span>
                      <div className="quantity-control">
                        <button 
                          onClick={() => setQuantity10kg(Math.max(0, quantity10kg - 1))}
                          disabled={quantity10kg <= 0}
                        >
                          -
                        </button>
                        <span className="quantity">{quantity10kg}</span>
                        <button onClick={() => setQuantity10kg(quantity10kg + 1)}>
                          +
                        </button>
                      </div>
                      <span className="subtotal">¬•{quantity10kg * price10kg}</span>
                    </div>

                    <div className="total-amount">
                      <strong>ÊÄªËÆ°Ôºö¬•{totalAmount}</strong>
                    </div>
                  </div>
                </div>

                {totalAmount > 0 && (
                  <div className="payment-section">
                    <div className="qr-code-area">
                      <div className="qr-placeholder">
                        <span>ÂæÆ‰ø°Êî∂Ê¨æÁ†Å</span>
                        <img src="/images/QR-code.png" alt="ÂæÆ‰ø°Êî∂Ê¨æÁ†Å" style={{width: '80%', height: '70%'}} />
                      </div>
                    </div>
                    
                    <div className="payment-instructions">
                      <h4>üí° ‰ªòÊ¨æËØ¥Êòé</h4>
                      <div className="instruction-item">
                        <span className="instruction-label">‰ªòÊ¨æÈáëÈ¢ùÔºö</span>
                        <span className="amount-highlight">¬•{totalAmount}</span>
                      </div>
                      <div className="instruction-item">
                        <span className="instruction-label">‰ªòÊ¨æÂ§áÊ≥®Ôºö</span>
                        <span className="note-highlight">ÂßìÂêç+ÊâãÊú∫Âè∑</span>
                      </div>
                      <p className="payment-note">
                        ‚ö†Ô∏è ËØ∑Âä°ÂøÖÂú®‰ªòÊ¨æÊó∂Â§áÊ≥®ÊÇ®ÁöÑ<strong>ÂßìÂêçÂíåÊâãÊú∫Âè∑</strong>Ôºå
                        ‰ª•‰æøÊàë‰ª¨Ê†∏ÂØπËÆ¢Âçï‰ø°ÊÅØ„ÄÇÁ°ÆËÆ§Êî∂Ê¨æÂêé24Â∞èÊó∂ÂÜÖÂèëË¥ßÔºÅ
                      </p>
                    </div>
                  </div>
                )}

                <div className="payment-buttons">
                  <button className="btn-secondary" onClick={handleBackToConfirm}>
                    ‚Üê ËøîÂõû‰∏ä‰∏ÄÊ≠•
                  </button>
                  <button className="btn-success" onClick={handleClose}>
                    üíö Â∑≤ÂÆåÊàê‰ªòÊ¨æ
                  </button>
                </div>
              </div>
            )}
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default OrderModal;
